func GetLanguageName(languageCode string, nameType NameType) string {
	languageCode = strings.ToLower(languageCode)
	var hitLanguage language

	if out, ok := codeMapping[languageCode]; ok {
		hitLanguage = out
	}

	if out, ok := threeCodeMapping[languageCode]; ok {
		hitLanguage = out
	}

	if out, ok := iso6392TMapping[languageCode]; ok {
		hitLanguage = out
	}

	if out, ok := iso6392BMapping[languageCode]; ok {
		hitLanguage = out
	}

	if nameType == NameTypeEnglish {
		return hitLanguage.English
	}

	return hitLanguage.Chinese
}
